// Extrair UID do payload
var cardUID = msg.payload.trim();  // Remover espaços em branco extras, se houver

// Exibir UID no console (opcional)
console.log("Card UID:", cardUID);

// Limpar UID, removendo caracteres não hexadecimais
cardUID = cardUID.replace(/[^0-9a-fA-F]/g, '');

// Validar UID (opcional)
if (!cardUID.match(/^[0-9a-fA-F]+$/)) {
    console.error("UID não é válido após a limpeza.");
    return null;  // Abortar a execução se o UID não for válido
}

// Criar a consulta SQL
var query = "SELECT saldo FROM users WHERE cardUID = '" + cardUID + "'";
msg.topic = "SELECT";
msg.payload = query;

console.log("Consulta SQL:", query);  // Adiciona esta linha para exibir a consulta no console

return msg;

var saldo = msg.payload[0].saldo;

// Exibir saldo no console (opcional)
console.log("Saldo:", saldo);

// Adicionar saldo ao payload
msg.payload = saldo;

return msg;
