} else {
    // Se não for o botão, criar a consulta SQL e enviar para a base de dados
    if (cardUID !== '' && cardUID.length === 8) {
        // Limpar UID, removendo caracteres não hexadecimais
        cardUID = cardUID.replace(/[^0-9a-fA-F]/g, '');
        global.set('cardUID', cardUID);

        // Criar a consulta SQL apenas se for um UID válido
        msg.topic = "SELECT Saldo, Username FROM users WHERE CardUID = '" + cardUID + "';";
